import React from 'react';

const ProgressBar = ({ currentStep }) => {
  const steps = [
    { key: 'input', label: 'Import List' },
    { key: 'mode-selection', label: 'Select Mode' },
    { key: 'ranking', label: 'Rank Movies' },
    { key: 'results', label: 'Results' }
  ];

  const currentIndex = steps.findIndex(s => s.key === currentStep);

  return (
    <div className="w-full bg-base-200 px-4 py-4 mb-8 border-b border-base-content/10">
      <div className="max-w-3xl mx-auto">
        <ul className="steps steps-horizontal w-full">
          {steps.map((step, index) => (
            <li 
              key={step.key} 
              className={`step ${index <= currentIndex ? 'step-primary' : ''}`}
              data-content={index <= currentIndex ? "✓" : (index + 1)}
            >
              <span className={`text-sm mt-2 ${index === currentIndex ? 'text-primary font-medium' : 'text-base-content/70'}`}>
                {step.label}
              </span>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ProgressBar;
