import React from 'react';
import { ArrowRight, MoveUp, MoveDown } from 'lucide-react';

const RankingPreview = ({ currentPair, rankings, onMovieClick }) => {
  if (!currentPair || currentPair.length !== 2) return null;

  // Get current sorted rankings
  const sortedRankings = Object.values(rankings)
    .sort((a, b) => b.rating - a.rating)
    .map(r => r.movie);

  // Find positions of current pair
  const positions = currentPair.map(movie => 
    sortedRankings.findIndex(m => m.identifier === movie.identifier)
  );

  // Get surrounding movies (2 above and 2 below the middle position)
  const middlePosition = Math.floor((positions[0] + positions[1]) / 2);
  const previewRange = [-2, -1, 0, 1, 2];
  const previewMovies = previewRange.map(offset => {
    const position = middlePosition + offset;
    return position >= 0 && position < sortedRankings.length ? {
      movie: sortedRankings[position],
      position: position + 1,
      isCurrent: currentPair.some(m => m.identifier === sortedRankings[position].identifier)
    } : null;
  }).filter(Boolean);

  return (
    <div className="hidden lg:block absolute right-4 top-1/2 -translate-y-1/2">
      <div className="bg-base-200/50 backdrop-blur-sm rounded-lg p-4 shadow-sm w-[280px]">
        <h3 className="text-sm font-medium mb-3 flex items-center gap-2 text-base-content/80">
          <ArrowRight className="w-4 h-4" />
          Current Rankings Context
        </h3>

        <div className="space-y-2">
          {previewMovies.map(({ movie, position, isCurrent }) => (
            <div
              key={movie.identifier}
              className={`flex items-center gap-3 p-2 rounded ${
                isCurrent ? 'bg-primary/10 text-primary' : 'hover:bg-base-200'
              } transition-colors`}
              onClick={() => onMovieClick?.(movie)}
            >
              <span className="text-sm font-mono w-6 text-base-content/50">
                {position}
              </span>

              <span className="flex-grow text-sm font-medium truncate" title={movie.title}>
                {movie.title}
              </span>

              {isCurrent && (
                <div className="flex items-center text-primary/70">
                  {position <= middlePosition ? <MoveUp className="w-4 h-4" /> : <MoveDown className="w-4 h-4" />}
                </div>
              )}
            </div>
          ))}
        </div>

        <p className="text-xs text-base-content/50 mt-4">
          Shows approximate ranking context for current comparison
        </p>
      </div>
    </div>
  );
};

export default RankingPreview;
