import React, { useMemo } from 'react';
import { Scale, Zap, AlertCircle } from 'lucide-react';

const GuidePanel = ({ movie, rankings, currentPair, direction = "right" }) => {
  // Early return wrapped in useMemo to avoid hook rules violation
  const shouldRender = useMemo(() => {
    if (!movie || !rankings || !currentPair) return false;
    return true;
  }, [movie, rankings, currentPair]);

  // Calculate all memoized values at the top level
  const decisionImpact = useMemo(() => {
    if (!shouldRender) return 0;

    const currentRating = rankings[movie.identifier].rating;
    const opponentMovie = currentPair.find(m => m.identifier !== movie.identifier);
    const opponentRating = rankings[opponentMovie.identifier].rating;
    const ratingDiff = Math.abs(currentRating - opponentRating);

    const comparisonCount = rankings[movie.identifier].comparisons;
    const confidence = rankings[movie.identifier].confidenceScore || 0.5;

    const impactScore = (1 / (1 + ratingDiff)) * 
                       Math.min(comparisonCount / 10, 1) *
                       (1 - confidence);

    return Math.min(Math.max(impactScore, 0), 1);
  }, [shouldRender, rankings, movie, currentPair]);

  const consistencyHint = useMemo(() => {
    if (!shouldRender) return null;
    if (!rankings[movie.identifier]) return null;

    const recentResults = rankings[movie.identifier].recentResults;
    if (recentResults.length < 3) return null;

    const recentWinRate = recentResults
      .slice(-3)
      .filter(r => r.result === 1).length / 3;

    if (recentWinRate > 0.66) {
      return { type: 'strong', message: 'Consistently wins similar comparisons' };
    } else if (recentWinRate < 0.33) {
      return { type: 'weak', message: 'Often ranked lower in similar comparisons' };
    }
    return { type: 'neutral', message: 'Mixed results in similar comparisons' };
  }, [shouldRender, rankings, movie]);

  if (!shouldRender) return null;

  return (
    <div className={`p-4 rounded-lg bg-base-200/50 backdrop-blur-sm ${
      direction === "left" ? "text-right" : "text-left"
    }`}>
      {/* Impact Indicator */}
      <div className={`flex items-center gap-2 mb-3 ${
        direction === "left" ? "justify-end" : "justify-start"
      }`}>
        <Scale className={`w-4 h-4 ${
          decisionImpact > 0.7 ? 'text-warning' : 
          decisionImpact > 0.4 ? 'text-primary' : 
          'text-base-content/50'
        }`} />
        <span className="text-sm font-medium">
          {decisionImpact > 0.7 ? 'Key Decision' :
           decisionImpact > 0.4 ? 'Notable Impact' :
           'Minor Impact'}
        </span>
      </div>

      {/* Consistency Hint */}
      {consistencyHint && (
        <div className={`flex items-center gap-2 ${
          direction === "left" ? "justify-end" : "justify-start"
        }`}>
          {consistencyHint.type === 'strong' ? (
            <Zap className="w-4 h-4 text-success" />
          ) : consistencyHint.type === 'weak' ? (
            <AlertCircle className="w-4 h-4 text-warning" />
          ) : (
            <div className="w-4 h-4" /> // Spacer for neutral
          )}
          <span className="text-sm text-base-content/70">
            {consistencyHint.message}
          </span>
        </div>
      )}
    </div>
  );
};

export default GuidePanel;
