import React from 'react';

const ProgressBar = ({ currentStep }) => {
  const steps = [
    { key: 'input', label: 'Import Movies' },
    { key: 'mode-selection', label: 'Select Mode' },
    { key: 'ranking', label: 'Compare Movies' },
    { key: 'results', label: 'Final Ranking' }
  ];

  const currentIndex = steps.findIndex(s => s.key === currentStep);

  return (
    <div className="fixed left-8 top-1/2 -translate-y-1/2 hidden lg:block">
      <div className="space-y-8">
        {steps.map((step, idx) => (
          <div 
            key={step.key}
            className={`flex items-center gap-3 transition-all duration-300
              ${idx === currentIndex 
                ? 'translate-x-2' 
                : idx < currentIndex 
                  ? 'opacity-50 -translate-x-1' 
                  : 'opacity-30'}`}
          >
            <div 
              className={`w-2 h-2 rounded-full transition-all duration-300
                ${idx === currentIndex 
                  ? 'bg-primary scale-150' 
                  : idx < currentIndex 
                    ? 'bg-primary/50' 
                    : 'bg-base-content/30'}`}
            />
            <span className={`text-sm whitespace-nowrap transition-colors duration-300
              ${idx === currentIndex 
                ? 'text-primary font-medium' 
                : 'text-base-content/50'}`}
            >
              {step.label}
            </span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProgressBar;
