import React, { useMemo } from 'react';
import { Timer, Sparkles, Zap, Info } from 'lucide-react';

const getPhaseInfo = (progress) => {
  if (progress < 0.3) {
    return {
      message: "Initial sorting...",
      icon: Zap,
      color: "text-primary/70"
    };
  } else if (progress < 0.7) {
    return {
      message: "Refining rankings...",
      icon: Sparkles,
      color: "text-primary"
    };
  } else {
    return {
      message: "Finalizing results...",
      icon: Timer,
      color: "text-primary/90"
    };
  }
};

const StatusBar = ({ 
  comparisons, 
  maxComparisons, 
  avgConfidence, 
  stabilityScore,
  estimatedMinutesLeft 
}) => {
  const progress = comparisons / maxComparisons;
  const phase = useMemo(() => getPhaseInfo(progress), [progress]);

  return (
    <div className="sticky bottom-0 w-full bg-base-200 border-t border-base-300 shadow-lg">
      <div className="container mx-auto px-4 py-3">
        <div className="flex items-center gap-6">
          {/* Left: Phase Indicator */}
          <div className="flex items-center gap-2 min-w-[180px]">
            <phase.icon className={`w-4 h-4 ${phase.color}`} />
            <span className="text-sm text-base-content/70">{phase.message}</span>
          </div>

          {/* Center: Progress */}
          <div className="flex-1">
            <div className="flex items-center gap-3">
              <div className="flex-1 h-2 bg-base-300 rounded-full overflow-hidden">
                <div className="h-full bg-primary transition-all duration-300 rounded-full relative overflow-hidden"
                     style={{ width: `${Math.min(100, Math.round(progress * 100))}%` }}>
                  <div className="absolute inset-0 bg-primary/50 animate-pulse" />
                </div>
              </div>
              <span className="text-sm font-medium min-w-[80px]">
                {comparisons} / {maxComparisons}
              </span>
            </div>

            {/* Stability Indicator */}
            <div className="flex items-center gap-2 mt-2">
              <div className="flex-1 h-1 bg-base-300/50 rounded-full overflow-hidden">
                <div 
                  className="h-full transition-all duration-300 rounded-full"
                  style={{ 
                    width: `${Math.min(100, Math.round(stabilityScore * 100))}%`,
                    backgroundColor: stabilityScore > 0.7 ? '#00ac1c' : '#ff8000'
                  }} 
                />
              </div>
              <span className="text-xs text-base-content/50">Stability</span>
            </div>
          </div>

          {/* Right: Stats */}
          <div className="flex items-center gap-4 min-w-[200px]">
            <div className="flex flex-col items-end">
              <div className="flex items-center gap-1">
                <span className="text-xs text-base-content/70">Confidence</span>
                <span className="font-medium">{Math.round(avgConfidence * 100)}%</span>
              </div>
              <div className="flex items-center gap-1">
                <span className="text-xs text-base-content/70">Time Left</span>
                <span className="font-medium">~{estimatedMinutesLeft}m</span>
              </div>
            </div>

            <button className="btn btn-ghost btn-sm px-2" title="Show Keyboard Controls">
              <Info className="w-4 h-4" />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default StatusBar;
