import React from 'react';
import { AlertCircle, ArrowDown, ArrowUp, BarChart2, Check, Sigma } from 'lucide-react';

const getConfidenceLevel = (score) => {
  if (score >= 0.8) return { label: 'Very High', color: 'text-success' };
  if (score >= 0.6) return { label: 'High', color: 'text-primary' };
  if (score >= 0.4) return { label: 'Moderate', color: 'text-warning' };
  return { label: 'Low', color: 'text-error' };
};

const generateNarrative = (stats) => {
  const winRate = (stats.wins / stats.comparisons * 100).toFixed(1);
  const confidenceLevel = getConfidenceLevel(stats.confidence);
  const recentWins = stats.recentResults.filter(r => r === 1).length;
  const recentTotal = stats.recentResults.length;
  const recentWinRate = (recentWins / recentTotal * 100).toFixed(1);

  return (
    `This movie won ${winRate}% of its ${stats.comparisons} comparisons. ` +
    `Recently, it won ${recentWins} out of its last ${recentTotal} matchups (${recentWinRate}%). ` +
    `Based on these results, we have ${confidenceLevel.label.toLowerCase()} confidence in its current ranking.`
  );
};

const StatBox = ({ label, value, icon: Icon, detail = null }) => (
    <div className="bg-base-300/50 rounded-lg p-4 flex flex-col gap-1">
      {/* Header with icon and label */}
      <div className="flex items-center gap-2 text-base-content/70">
        <Icon className="w-4 h-4" />
        <span className="text-sm font-medium">{label}</span>
      </div>

      {/* Value and detail in column layout */}
      <div className="flex flex-col">
        <span className="text-2xl font-bold">{value}</span>
        {detail && (
          <span className="text-sm text-base-content/60 mt-0.5">{detail}</span>
        )}
      </div>
    </div>
  );

const MovieStatsPanel = ({ 
  movie, 
  stats: { 
    rating, 
    wins, 
    losses, 
    comparisons, 
    recentResults,
    confidence,
    uncertainty
  }
}) => {
  const winRate = ((wins / comparisons) * 100).toFixed(1);
  const confidenceLevel = getConfidenceLevel(confidence);
  const narrative = generateNarrative({ wins, comparisons, confidence, recentResults });

  return (
    <div className="py-6 px-4 bg-base-200/50 rounded-lg animate-[slideUp_0.3s_ease-out]">
      {/* Narrative Section */}
      <div className="mb-6 text-base-content/80 leading-relaxed">
        {narrative}
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
        <StatBox 
            label="Win Rate"
            value={`${winRate}%`}
            icon={ArrowUp}
            detail={`${wins} wins, ${losses} losses`}
        />
        <StatBox 
            label="Comparisons" 
            value={comparisons}
            icon={Sigma}
        />
        <StatBox 
            label="Confidence"
            value={confidenceLevel.label}
            icon={AlertCircle}
            detail={`${(confidence * 100).toFixed(1)}%`}
        />
        <StatBox 
            label="Trend"     
            value={`${(recentResults.filter(r => r === 1).length / recentResults.length * 100).toFixed(1)}%`}
            icon={BarChart2}
            detail={`Last ${recentResults.length} matches`}
        />
        </div>

      {/* Recent Results Timeline */}
      <div className="space-y-2">
        <h4 className="text-sm font-medium text-base-content/70">Recent Matchups</h4>
        <div className="flex gap-1">
          {recentResults.map((result, i) => (
            <div 
              key={i}
              className={`h-1 flex-1 rounded-full ${
                result ? 'bg-success' : 'bg-error'
              }`}
              title={result ? 'Won' : 'Lost'}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default MovieStatsPanel;
