import React from 'react';
import { ArrowUp, ArrowDown, Info } from 'lucide-react';

const MovieRankContext = ({ movie, rankings, position, direction = "right" }) => {
  if (!movie || !rankings) return null;

  // Get current sorted rankings
  const sortedRankings = Object.values(rankings)
    .sort((a, b) => b.rating - a.rating)
    .map(r => r.movie);

  // Get 2 movies above and 2 below
  const moviePosition = position ?? sortedRankings.findIndex(m => m.identifier === movie.identifier);
  const previewRange = [-2, -1, 0, 1, 2];
  const previewMovies = previewRange.map(offset => {
    const pos = moviePosition + offset;
    return pos >= 0 && pos < sortedRankings.length ? {
      movie: sortedRankings[pos],
      position: pos + 1,
      isCurrent: movie.identifier === sortedRankings[pos].identifier
    } : null;
  }).filter(Boolean);

  return (
    <div className={`bg-base-200/50 backdrop-blur-sm rounded-lg p-4 shadow-sm w-[280px] ${
      direction === "left" ? "text-right" : "text-left"
    }`}>
      <h3 className={`text-sm font-medium mb-3 flex items-center gap-2 text-base-content/80 ${
        direction === "left" ? "justify-end" : "justify-start"
      }`}>
        {direction === "left" && (
          <Info className="w-4 h-4" />
        )}
        Current Ranking
        {direction === "right" && (
          <Info className="w-4 h-4" />
        )}
      </h3>

      <div className="space-y-2">
        {previewMovies.map(({ movie: previewMovie, position: previewPosition, isCurrent }) => (
          <div
            key={previewMovie.identifier}
            className={`flex items-center gap-3 p-2 rounded ${
              isCurrent ? 'bg-primary/10 text-primary' : 'hover:bg-base-200'
            } transition-colors ${
              direction === "left" ? "flex-row-reverse" : "flex-row"
            }`}
          >
            <span className="text-sm font-mono w-6 text-base-content/50">
              {previewPosition}
            </span>

            <span className="flex-grow text-sm font-medium truncate" title={previewMovie.title}>
              {previewMovie.title}
            </span>

            {isCurrent && (
              <div className="text-primary/70">
                {previewPosition <= moviePosition ? 
                  <ArrowUp className="w-4 h-4" /> : 
                  <ArrowDown className="w-4 h-4" />
                }
              </div>
            )}
          </div>
        ))}
      </div>

      <p className="text-xs text-base-content/50 mt-4">
        Shows current position in overall ranking
      </p>
    </div>
  );
};

export default MovieRankContext;
