import React from 'react';
import './MovieResults.css';

const MovieResults = ({ rankings }) => {
  if (!rankings || rankings.length === 0) {
    return (
      <div className="results-empty">
        <p>No movie rankings available.</p>
      </div>
    );
  }

  const sortedMovies = [...rankings].sort((a, b) => b.rating - a.rating);

  const handleExport = () => {
    const csvContent = [
      ['Rank', 'Title', 'Year'],
      ...sortedMovies.map((rankingData, index) => [
        index + 1,
        rankingData.movie.title,
        rankingData.movie.year
      ])
    ].map(row => row.join(',')).join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'movie-rankings.csv';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    window.URL.revokeObjectURL(url);
  };

  return (
    <div className="results-container">
      <div className="results-header">
        <h2>Your Movie Rankings</h2>
        <button onClick={handleExport} className="export-button">
          Export to CSV
        </button>
      </div>

      <div className="movie-grid">
        {sortedMovies.map((rankingData, index) => (
          <div 
            key={rankingData.movie.identifier}
            className="movie-card"
          >
            <div className="movie-image-container">
              <div className="movie-rank">#{index + 1}</div>
              <img 
                src={rankingData.movie.poster} 
                alt={rankingData.movie.title}
                className="movie-image"
              />
            </div>
            <div className="movie-info">
              <h3 title={rankingData.movie.title}>
                {rankingData.movie.title}
              </h3>
              <div className="movie-details">
                <span>{rankingData.movie.year}</span>
                <span>Score: {rankingData.rating.toFixed(2)}</span>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MovieResults;
