import React from 'react';
import { motion } from 'framer-motion';
import { Download } from 'lucide-react';

const MovieResults = ({ rankings }) => {
  const sortedMovies = [...rankings].sort((a, b) => b.rating - a.rating);

  const exportToCsv = () => {
    const csvContent = [
      ['Rank', 'Title', 'Year', 'Rating'],
      ...sortedMovies.map((rankingData, index) => [
        index + 1,
        rankingData.movie.title,
        rankingData.movie.year,
        rankingData.rating.toFixed(2)
      ])
    ].map(row => row.join(',')).join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'movie-rankings.csv';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    window.URL.revokeObjectURL(url);
  };

  return (
    <div className="container mx-auto px-4 py-8 max-w-4xl">
      {/* Header */}
      <div className="flex justify-between items-center mb-8">
        <h2 className="text-3xl font-bold">Your Movie Rankings</h2>
        <button
          onClick={exportToCsv}
          className="btn btn-primary btn-sm gap-2"
        >
          <Download className="w-4 h-4" />
          Export
        </button>
      </div>

      {/* Results List */}
      <div className="space-y-4">
        {sortedMovies.map((rankingData, index) => (
          <motion.div
            key={rankingData.movie.identifier}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3, delay: index * 0.05 }}
            className="flex gap-4 p-4 bg-base-200 rounded-lg hover:bg-base-300 transition-colors"
          >
            <div className="w-16 h-24 flex-shrink-0">
              <img
                src={rankingData.movie.poster || '/api/placeholder/400/600'}
                alt={rankingData.movie.title}
                className="w-full h-full object-cover rounded"
              />
            </div>

            <div className="flex-grow flex items-center">
              <div className="mr-4 w-12 text-center">
                <span className="font-bold text-lg text-primary">#{index + 1}</span>
              </div>

              <div className="flex-grow">
                <h3 className="font-bold text-lg leading-tight">
                  {rankingData.movie.title}
                </h3>
                <div className="text-sm text-base-content/70 flex items-center gap-4">
                  <span>{rankingData.movie.year}</span>
                  <span className="text-base-content/50">â€¢</span>
                  <span>Score: {rankingData.rating.toFixed(2)}</span>
                </div>
              </div>
            </div>
          </motion.div>
        ))}
      </div>
    </div>
  );
};

export default MovieResults;
