import React from 'react';

const ProgressBar = ({ currentStep }) => {
  const getCurrentInfo = () => {
    switch(currentStep) {
      case 'input':
        return { step: 1, label: 'Import List' };
      case 'mode-selection':
        return { step: 2, label: 'Select Mode' };
      case 'ranking':
        return { step: 3, label: 'Compare Movies' };
      case 'results':
        return { step: 4, label: 'Final Ranking' };
      default:
        return { step: 1, label: 'Import List' };
    }
  };

  const { step, label } = getCurrentInfo();

  return (
    <div className="mt-4 mb-8 flex justify-center">
      <div className="px-4 py-1.5 bg-base-200 rounded-full flex items-center gap-3">
        <div className="flex items-center gap-1.5">
          <div className="w-2 h-2 rounded-full bg-primary animate-pulse" />
          <span className="text-sm font-medium">Step {step}</span>
        </div>
        <span className="text-sm text-base-content/70">{label}</span>
      </div>
    </div>
  );
};

export default ProgressBar;
