import React from 'react';
import { Check } from 'lucide-react';

const ProgressBar = ({ currentStep }) => {
  const steps = [
    { key: 'input', label: 'Import' },
    { key: 'mode-selection', label: 'Mode' },
    { key: 'ranking', label: 'Rank' },
    { key: 'results', label: 'Results' }
  ];

  const currentIndex = steps.findIndex(s => s.key === currentStep);

  return (
    <nav className="w-full bg-base-200/50 border-b border-base-300">
      <div className="max-w-3xl mx-auto px-4 py-4">
        <div className="relative flex items-center justify-between">
          {/* Progress line */}
          <div className="absolute left-0 top-1/2 h-0.5 bg-base-300 -translate-y-1/2 w-full -z-10" />
          <div 
            className="absolute left-0 top-1/2 h-0.5 bg-primary/50 -translate-y-1/2 transition-all duration-500 -z-10"
            style={{ 
              width: `${(currentIndex / (steps.length - 1)) * 100}%`
            }} 
          />

          {/* Steps */}
          {steps.map((step, idx) => (
            <div key={step.key} className="flex flex-col items-center gap-2">
              <div 
                className={`w-8 h-8 rounded-full flex items-center justify-center transition-colors duration-300 
                  ${idx <= currentIndex 
                    ? 'bg-primary text-primary-content' 
                    : 'bg-base-300 text-base-content/50'}`}
              >
                {idx < currentIndex ? (
                  <Check className="w-4 h-4" />
                ) : (
                  <span className="text-sm font-medium">{idx + 1}</span>
                )}
              </div>
              <span className={`text-xs font-medium transition-colors duration-300
                ${idx === currentIndex 
                  ? 'text-primary' 
                  : idx < currentIndex 
                    ? 'text-base-content/70' 
                    : 'text-base-content/40'}`}
              >
                {step.label}
              </span>
            </div>
          ))}
        </div>
      </div>
    </nav>
  );
};

export default ProgressBar;
