import React from 'react';
import { motion } from 'framer-motion';
import { CheckCircle2, Circle } from 'lucide-react';

const ProgressBar = ({ currentStep }) => {
  const steps = [
    { key: 'input', label: 'Import List' },
    { key: 'mode-selection', label: 'Select Mode' },
    { key: 'ranking', label: 'Rank Movies' },
    { key: 'results', label: 'Results' }
  ];

  const currentIndex = steps.findIndex(s => s.key === currentStep);

  return (
    <div className="w-full bg-base-200 px-4 py-6 mb-8 border-b border-base-content/10">
      <div className="max-w-3xl mx-auto">
        <div className="relative flex justify-between">
          {/* Progress Line */}
          <div className="absolute top-5 left-0 w-full h-0.5 bg-base-300">
            <motion.div 
              className="h-full bg-primary"
              initial={{ width: 0 }}
              animate={{ width: `${(currentIndex / (steps.length - 1)) * 100}%` }}
              transition={{ duration: 0.5, ease: "easeInOut" }}
            />
          </div>

          {/* Steps */}
          {steps.map((step, index) => (
            <div key={step.key} className="relative flex flex-col items-center">
              <motion.div
                initial={{ scale: 0.8, opacity: 0 }}
                animate={{ 
                  scale: 1, 
                  opacity: 1 
                }}
                transition={{ delay: index * 0.1 }}
                className="relative z-10"
              >
                {index <= currentIndex ? (
                  <div className="w-10 h-10 rounded-full bg-primary flex items-center justify-center">
                    <CheckCircle2 className="w-5 h-5 text-primary-content" />
                  </div>
                ) : (
                  <div className="w-10 h-10 rounded-full bg-base-300 flex items-center justify-center">
                    <Circle className="w-5 h-5 text-base-content/50" />
                  </div>
                )}
              </motion.div>

              <motion.div
                initial={{ y: 10, opacity: 0 }}
                animate={{ 
                  y: 0, 
                  opacity: 1
                }}
                transition={{ delay: index * 0.1 + 0.2 }}
                className={`mt-2 text-sm font-medium ${
                  index === currentIndex 
                    ? 'text-primary' 
                    : index < currentIndex 
                      ? 'text-base-content/70'
                      : 'text-base-content/50'
                }`}
              >
                {step.label}
              </motion.div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ProgressBar;
