import React from 'react';
import { motion } from 'framer-motion';
import { CheckCircle2, Circle } from 'lucide-react';

const ProgressBar = ({ currentStep }) => {
  const steps = [
    { key: 'input', label: 'Import List' },
    { key: 'mode-selection', label: 'Select Mode' },
    { key: 'ranking', label: 'Rank Movies' },
    { key: 'results', label: 'Results' }
  ];

  const currentIndex = steps.findIndex(s => s.key === currentStep);

  return (
    <div className="w-full px-4 py-6 mb-4">
      <div className="max-w-2xl mx-auto">
        <div className="relative">
          {/* Icons row with segmented lines */}
          <div className="relative flex justify-between mb-2">
            {/* Step Icons */}
            {steps.map((step, index) => (
              <div key={`icon-wrapper-${step.key}`} className="relative">
                {/* Line to next step (if not last step) */}
                {index < steps.length - 1 && (
                  <div 
                    className="absolute top-1/2 left-1/2 h-0.5 bg-base-300 -z-10"
                    style={{
                      right: '-50%',
                      transform: 'translateY(-50%)',
                    }}
                  >
                    {index < currentIndex && (
                      <motion.div 
                        className="h-full bg-primary/50"
                        initial={{ width: 0 }}
                        animate={{ width: '100%' }}
                        transition={{ duration: 0.5, ease: "easeInOut" }}
                      />
                    )}
                  </div>
                )}

                {/* Icon */}
                <motion.div
                  initial={{ scale: 0.8, opacity: 0 }}
                  animate={{ scale: 1, opacity: 1 }}
                  transition={{ delay: index * 0.1 }}
                  className="relative z-10"
                >
                  {index <= currentIndex ? (
                    <div className="w-8 h-8 rounded-full bg-primary/10 flex items-center justify-center">
                      <CheckCircle2 className="w-4 h-4 text-primary" />
                    </div>
                  ) : (
                    <div className="w-8 h-8 rounded-full bg-base-300/50 flex items-center justify-center">
                      <Circle className="w-4 h-4 text-base-content/30" />
                    </div>
                  )}
                </motion.div>
              </div>
            ))}
          </div>

          {/* Labels row */}
          <div className="flex justify-between px-0">
            {steps.map((step, index) => (
              <motion.div
                key={`label-${step.key}`}
                initial={{ y: 10, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                transition={{ delay: index * 0.1 + 0.2 }}
                className={`text-xs font-medium text-center w-8 ${
                  index === currentIndex 
                    ? 'text-primary' 
                    : index < currentIndex 
                      ? 'text-base-content/70'
                      : 'text-base-content/30'
                }`}
              >
                {step.label}
              </motion.div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProgressBar;
