import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Download, Filter, ChevronUp, ChevronDown, Search } from 'lucide-react';

const MovieResults = ({ rankings }) => {
  const [filterYear, setFilterYear] = useState('all');
  const [searchQuery, setSearchQuery] = useState('');
  const [sortOrder, setSortOrder] = useState('rank'); // 'rank', 'year', 'rating'
  const [showFilters, setShowFilters] = useState(false);

  // Get unique years for filter
  const years = [...new Set(rankings.map(r => r.movie.year))].sort((a, b) => b - a);

  // Filter and sort movies
  const filteredMovies = rankings
    .filter(rankingData => {
      const matchesYear = filterYear === 'all' || rankingData.movie.year.toString() === filterYear;
      const matchesSearch = rankingData.movie.title.toLowerCase().includes(searchQuery.toLowerCase());
      return matchesYear && matchesSearch;
    })
    .sort((a, b) => {
      switch (sortOrder) {
        case 'year':
          return b.movie.year - a.movie.year;
        case 'rating':
          return b.rating - a.rating;
        default:
          return b.rating - a.rating;
      }
    });

  const exportToCsv = () => {
    const csvContent = [
      ['Rank', 'Title', 'Year', 'Rating'],
      ...filteredMovies.map((rankingData, index) => [
        index + 1,
        rankingData.movie.title,
        rankingData.movie.year,
        rankingData.rating.toFixed(2)
      ])
    ].map(row => row.join(',')).join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'movie-rankings.csv';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    window.URL.revokeObjectURL(url);
  };

  return (
    <div className="container mx-auto px-4 py-8">
      {/* Header */}
      <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4 mb-8">
        <h2 className="text-3xl font-bold">Your Movie Rankings</h2>
        <button
          onClick={exportToCsv}
          className="btn btn-primary gap-2"
        >
          <Download className="w-4 h-4" />
          Export Rankings
        </button>
      </div>

      {/* Filters */}
      <div className="mb-8 space-y-4">
        <div className="flex flex-col sm:flex-row gap-4">
          <div className="flex-1">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-base-content/50" />
              <input
                type="text"
                placeholder="Search movies..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="input input-bordered w-full pl-10"
              />
            </div>
          </div>
          <button
            onClick={() => setShowFilters(!showFilters)}
            className="btn btn-outline gap-2"
          >
            <Filter className="w-4 h-4" />
            Filters
          </button>
        </div>

        {showFilters && (
          <motion.div
            initial={{ height: 0, opacity: 0 }}
            animate={{ height: 'auto', opacity: 1 }}
            exit={{ height: 0, opacity: 0 }}
            transition={{ duration: 0.2 }}
            className="p-4 bg-base-200 rounded-lg space-y-4"
          >
            <div className="flex flex-wrap gap-4">
              <div className="form-control">
                <label className="label">
                  <span className="label-text">Year</span>
                </label>
                <select
                  value={filterYear}
                  onChange={(e) => setFilterYear(e.target.value)}
                  className="select select-bordered"
                >
                  <option value="all">All Years</option>
                  {years.map(year => (
                    <option key={year} value={year}>{year}</option>
                  ))}
                </select>
              </div>

              <div className="form-control">
                <label className="label">
                  <span className="label-text">Sort By</span>
                </label>
                <select
                  value={sortOrder}
                  onChange={(e) => setSortOrder(e.target.value)}
                  className="select select-bordered"
                >
                  <option value="rank">Rank</option>
                  <option value="year">Year</option>
                  <option value="rating">Rating</option>
                </select>
              </div>
            </div>
          </motion.div>
        )}
      </div>

      {/* Results Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
        {filteredMovies.map((rankingData, index) => (
          <motion.div
            key={rankingData.movie.identifier}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3, delay: index * 0.05 }}
            className="card bg-base-200 shadow-xl overflow-hidden group hover:-translate-y-1 transition-transform"
          >
            <figure className="relative aspect-[2/3]">
              <img
                src={rankingData.movie.poster || '/api/placeholder/400/600'}
                alt={rankingData.movie.title}
                className="w-full h-full object-cover"
              />
              <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-black/40 to-transparent opacity-0 group-hover:opacity-100 transition-opacity" />
              <div className="absolute top-2 left-2">
                <div className="badge badge-primary font-bold">#{index + 1}</div>
              </div>
            </figure>
            <div className="card-body p-4">
              <h3 className="card-title text-lg leading-tight" title={rankingData.movie.title}>
                {rankingData.movie.title}
              </h3>
              <div className="flex justify-between items-center mt-2 text-sm text-base-content/70">
                <span>{rankingData.movie.year}</span>
                <div className="flex items-center gap-1">
                  <span>Score:</span>
                  <span className="font-medium text-base-content">
                    {rankingData.rating.toFixed(2)}
                  </span>
                </div>
              </div>
            </div>
          </motion.div>
        ))}
      </div>

      {filteredMovies.length === 0 && (
        <div className="text-center py-12">
          <p className="text-lg text-base-content/70">No movies found matching your criteria.</p>
        </div>
      )}
    </div>
  );
};

export default MovieResults;
