import React from 'react';

const ProgressBar = ({ currentStep }) => {
  const steps = [
    { key: 'input', label: 'Import List' },
    { key: 'mode-selection', label: 'Select Mode' },
    { key: 'ranking', label: 'Compare Movies' },
    { key: 'results', label: 'Final Ranking' }
  ];

  const currentIndex = steps.findIndex(s => s.key === currentStep);

  return (
    <div className="mt-4 mb-8 flex justify-center">
      <div className="px-4 py-2 bg-base-200 rounded-full flex items-center gap-4">
        {/* Current step info */}
        <div className="flex items-center gap-1.5">
          <div className="w-2 h-2 rounded-full bg-primary animate-pulse" />
          <span className="text-sm font-medium">
            {steps[currentIndex]?.label}
          </span>
        </div>

        {/* Step indicators */}
        <div className="flex items-center gap-2">
          {steps.map((step, idx) => (
            <div
              key={step.key}
              className={`w-1.5 h-1.5 rounded-full transition-all duration-300 
                ${idx === currentIndex 
                  ? 'bg-primary scale-125' 
                  : idx < currentIndex 
                    ? 'bg-primary/50' 
                    : 'bg-base-300'
                } ${idx === currentIndex ? 'cursor-default' : 'cursor-help'}`}
              title={step.label}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default ProgressBar;
