import React from 'react';
import {Info, ArrowRight, MoveUp, MoveDown } from 'lucide-react';

const RankingPreview = ({ currentPair, rankings, onMovieClick }) => {
  if (!currentPair || currentPair.length !== 2) return null;

  // Get current sorted rankings
  const sortedRankings = Object.values(rankings)
    .sort((a, b) => b.rating - a.rating)
    .map(r => r.movie);

  // Find positions of current pair
  const positions = currentPair.map(movie => 
    sortedRankings.findIndex(m => m.identifier === movie.identifier)
  );

  // Get surrounding movies (2 above and 2 below the middle position)
  const middlePosition = Math.floor((positions[0] + positions[1]) / 2);
  const previewRange = [-2, -1, 0, 1, 2];
  const previewMovies = previewRange.map(offset => {
    const position = middlePosition + offset;
    return position >= 0 && position < sortedRankings.length ? {
      movie: sortedRankings[position],
      position: position + 1,
      isCurrent: currentPair.some(m => m.identifier === sortedRankings[position].identifier)
    } : null;
  }).filter(Boolean);

  return (
    <div className="hidden lg:block absolute right-4 top-1/2 -translate-y-1/2">
      <div className="bg-base-200/50 backdrop-blur-sm rounded-lg p-4 shadow-sm w-[280px]">
        <div className="relative group">
          <h3 className="text-sm font-medium mb-3 flex items-center gap-2 text-base-content/80">
            <Info className="w-4 h-4" />
            Current Rankings Range
            <div className="absolute invisible group-hover:visible opacity-0 group-hover:opacity-100 
                        left-0 bottom-full mb-2 p-3 bg-base-300 rounded-lg text-xs shadow-lg z-50 w-[200px]">
              Shows how your choice will affect the current rankings. The highlighted items are being compared.
              <div className="absolute left-4 bottom-0 translate-y-1/2 rotate-45 w-2 h-2 bg-base-300" />
            </div>
          </h3>
        </div>

        <div className="relative">
          <div className="absolute left-8 top-0 bottom-0 w-px bg-base-content/10" />
          <div className="space-y-2">
            {previewMovies.map(({ movie, position, isCurrent }) => (
              <div
                key={movie.identifier}
                className={`flex items-center gap-3 p-2 rounded transition-all duration-200 ${
                  isCurrent 
                    ? 'bg-primary/10 text-primary shadow-sm relative z-10' 
                    : 'hover:bg-base-200/50'
                }`}
                onClick={() => onMovieClick?.(movie)}
              >
                <span className={`text-sm font-mono w-6 ${
                  isCurrent ? 'text-primary' : 'text-base-content/50'
                }`}>
                  {position}
                </span>

                <div className={`flex-grow flex items-center gap-2 ${
                  isCurrent ? 'font-medium' : ''
                }`}>
                  <span className="text-sm truncate" title={movie.title}>
                    {movie.title}
                  </span>
                  {isCurrent && (
                    <div className="flex-shrink-0">
                      {position <= middlePosition ? (
                        <div className="text-success text-xs">▲</div>
                      ) : (
                        <div className="text-error text-xs">▼</div>
                      )}
                    </div>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>

        <p className="text-xs text-base-content/50 mt-4 leading-relaxed">
          Movies being compared are highlighted. Arrows indicate potential movement direction based on your choice.
        </p>
      </div>
    </div>
  );
};

export default RankingPreview;
