import React from 'react';

const ProgressBar = ({ currentStep }) => {
  const steps = [
    { key: 'input', label: 'Import List' },
    { key: 'mode-selection', label: 'Select Mode' },
    { key: 'ranking', label: 'Compare Movies' },
    { key: 'results', label: 'Final Ranking' }
  ];

  const currentIndex = steps.findIndex(s => s.key === currentStep);

  return (
    <div className="mt-4 mb-8 flex justify-center">
      <div className="relative px-4 py-2 bg-base-200 rounded-full">
        {/* Dots container */}
        <div className="flex items-center gap-16">
          {steps.map((step, idx) => (
            <div
              key={step.key}
              className={`w-2 h-2 rounded-full transition-all duration-300 
                ${idx === currentIndex 
                  ? 'bg-primary scale-125' 
                  : idx < currentIndex 
                    ? 'bg-primary/50' 
                    : 'bg-base-300'}
                cursor-help`}
              title={step.label}
            />
          ))}
        </div>

        {/* Sliding text */}
        <div 
          className="absolute top-1/2 -translate-y-1/2 transition-all duration-300 whitespace-nowrap"
          style={{ 
            left: `${currentIndex * 64 + 16}px`, // 64px is the gap between dots (16px) + some offset
          }}
        >
          <span className="text-sm font-medium px-2 bg-base-200">
            {steps[currentIndex]?.label}
          </span>
        </div>
      </div>
    </div>
  );
};

export default ProgressBar;
