import React from 'react';

const ProgressBar = ({ currentStep }) => {
  const steps = {
    'input': 1,
    'mode-selection': 2,
    'ranking': 3,
    'results': 4
  };

  const currentStepNumber = steps[currentStep] || 1;
  const progress = ((currentStepNumber - 1) / 3) * 100;

  return (
    <div className="fixed top-0 left-0 right-0 z-50">
      {/* Progress bar */}
      <div className="h-1 bg-base-300">
        <div 
          className="h-full bg-primary transition-all duration-500 ease-out"
          style={{ width: `${progress}%` }}
        />
      </div>

      {/* Step indicator */}
      <div className="bg-base-200/80 backdrop-blur-sm border-b border-base-300">
        <div className="max-w-screen-xl mx-auto px-4 py-2 text-sm flex justify-between items-center">
          <span className="font-medium">
            Step {currentStepNumber} of 4
          </span>
          <span className="text-base-content/70">
            {currentStep === 'input' && 'Import Your List'}
            {currentStep === 'mode-selection' && 'Choose Ranking Mode'}
            {currentStep === 'ranking' && 'Rank Your Movies'}
            {currentStep === 'results' && 'View Results'}
          </span>
        </div>
      </div>
    </div>
  );
};

export default ProgressBar;
