import React, { useState, useEffect, useCallback } from 'react';
import { CheckCircle, XCircle, Link as LinkIcon } from 'lucide-react';

function InputForm({ onSubmit }) {
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [validationState, setValidationState] = useState('initial'); // 'initial', 'valid', 'invalid'
  const [errorMessage, setErrorMessage] = useState('');

  const isValidUrl = (string) => {
    try {
      const url = new URL(string);
      return url.protocol === 'http:' || url.protocol === 'https:';
    } catch (_) {
      return false;
    }
  };

  const validateInput = useCallback((value) => {
    if (!value) {
      setValidationState('initial');
      setErrorMessage('');
    } else if (!value.startsWith('http://') && !value.startsWith('https://')) {
      setValidationState('invalid');
      setErrorMessage('URL must start with http:// or https://');
    } else if (!isValidUrl(value)) {
      setValidationState('invalid');
      setErrorMessage('Please enter a valid URL');
    } else {
      setValidationState('valid');
      setErrorMessage('');
    }
  }, []);

  useEffect(() => {
    const timeoutId = setTimeout(() => {
      validateInput(input);
    }, 300); // Debounce validation

    return () => clearTimeout(timeoutId);
  }, [input, validateInput]);

  const handleFetchMovies = async () => {
    setLoading(true);
    setErrorMessage('');
    try {
      const response = await fetch(`http://localhost:3001/fetch-movies?url=${encodeURIComponent(input)}`);
      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Failed to fetch movies');
      }

      const { data: { movies } } = data;

      if (!movies?.length) {
        throw new Error('No movies found in the provided URL');
      }
      if (movies.length < 2) {
        throw new Error('Please enter a link with at least 2 movies');
      }
      onSubmit(movies);
    } catch (error) {
      console.error("Error fetching movies:", error);
      setErrorMessage(error.message || "Failed to fetch movies. Please check the URL and try again.");
      setValidationState('invalid');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!input) {
      setErrorMessage("Please enter a URL.");
      setValidationState('invalid');
      return;
    }
    if (!isValidUrl(input)) {
      setErrorMessage("Please enter a valid URL.");
      setValidationState('invalid');
      return;
    }
    handleFetchMovies();
  };

  return (
    <form onSubmit={handleSubmit} className="input-container">
      <div className="url-input-wrapper">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Enter the URL with the movie list (e.g., https://letterboxd.com/username/list/...)"
          className={`url-input ${validationState !== 'initial' ? validationState : ''}`}
          disabled={loading}
          autoComplete="off"
          autoCorrect="off"
          autoCapitalize="none"
          spellCheck="false"
          data-form-type="other"
          data-lpignore="true"
        />
        <span className={`input-icon ${validationState}`}>
          {validationState === 'valid' && <CheckCircle size={20} />}
          {validationState === 'invalid' && <XCircle size={20} />}
          {validationState === 'initial' && <LinkIcon size={20} />}
        </span>
      </div>
      <div className={`validation-message ${errorMessage ? 'visible' : ''}`}>
        {errorMessage}
      </div>
      <button 
        type="submit" 
        disabled={loading || validationState !== 'valid'}
        style={{ marginTop: '30px' }}
      >
        {loading ? "Loading..." : "Start Ranking"}
      </button>
    </form>
  );
}

export default InputForm;
